INSERT INTO [dbo].[Product] ([ID], [Name], [Category], [Price], [ImageSrc], [ImageAlt], [DetailProduktu]) VALUES (16, N'ASUS TUF GAMING B550-PLUS', N'Motherboard', CAST(3489.00 AS Decimal(18, 2)), N'/images\Products\ASUS TUF GAMING B550-PLUS.jfif', N'ASUS TUF GAMING B550-PLUS', N'TUF Gaming B550-Plus destiluje důležité prvky nejnovější AMD platformy a kombinuje je s herními funkcemi a prokázanou výdrží. Vytvořena s komponenty armádní kvality, vylepšeným napájecím řešením a srozumitelnou sadou chladících funkcí, tato základní deska přináší skálopevný výkon s nepolevující herní stabilitou.')
INSERT INTO [dbo].[Product] ([ID], [Name], [Category], [Price], [ImageSrc], [ImageAlt], [DetailProduktu]) VALUES (17, N'ASUS PRIME Z490-P', N'Motherboard', CAST(3199.00 AS Decimal(18, 2)), N'/images\Products\ASUS PRIME Z490-P.jfif', N'ASUS PRIME Z490-P', N'ASUS Prime série je expertně sestrojena pro využití plného potenciálu 10. generace Intel Core procesorů. Vybaveny robustním designem napájení, srozumitelnými řešeními chlazení a inteligentními možnostmi tuningu, základní desky série Z490 poskytují běžným uživatelům a stavitelům sestav řadu možností vylepšení výkonu, skrze intuitivní funkce softwaru a firmwaru.')
