INSERT INTO [dbo].[Product] ([ID], [Name], [Category], [Price], [ImageSrc], [ImageAlt], [DetailProduktu]) VALUES (19, N'Kingston Now A400, 2,5" - 480GB', N'Storage', CAST(1375.00 AS Decimal(18, 2)), N'/images\Products\Kingston Now A400.jfif', N'Kingston Now A400', N'SSD jednotka značky Kingston dramaticky vylepšuje odezvu vašeho počítačového systému - zrychluje jeho start, nahrávání aplikací a přenosovou rychlost dat. Je poháněn nejmodernějším řadičem pro čtení a zápis datových balíků. SSD je díky tomu rychlejší až 10x ve srovnání s tradičním harddiskem. To vše se příznivě projevuje také na příkladném multitaskingu, který nabízí celý systém. ')
INSERT INTO [dbo].[Product] ([ID], [Name], [Category], [Price], [ImageSrc], [ImageAlt], [DetailProduktu]) VALUES (20, N'Kingston A2000, M.2 - 1TB', N'Storage', CAST(2799.00 AS Decimal(18, 2)), N'/images\Products\Kingston A2000, M.2 - 1TB.jfif', N'Kingston A2000, M.2 - 1TB', N'SSD disk A2000 NVMe PCIe značky Kingston představuje cenově dostupné řešení s působivým výkonem. S vynikajícími rychlostmi čtení/zápisu disk A2000 nabízí 3x vyšší výkon než SSD disk SATA, a to díky kratší době načítání, menší spotřebě energie a menšímu zahřívání. SSD disk A2000 je určen pro uživatele na základní úrovni, výrobce účelových systémů, výrobce vlastních systémů a pro ty, kteří chtějí zvýšit výkon svého PC. Jednostranná tenká konstrukce M.2 umožňuje disku A2000 pracovat na plný výkon v ultrabooku i v PC systému v malém provedení (SFF PC).')
